import React from "react";
import PropTypes from "prop-types";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

const ProductAdd = ({ onAdd }) => {
  const { register, handleSubmit, errors } = useForm();
  let history = useHistory();
  const onHandleSubmit = (data) => {
    onAdd(data);
    history.push("/admin/products");
  };

  return (
    <div className="content p-4">
      <div className="container">
        <div className="card card-primary">
          <div className="card-header">
            <h3 className="card-title">Thêm sản phẩm</h3>
          </div>
          <form onSubmit={handleSubmit(onHandleSubmit)}>
            <div className="card-body">
              <div className="form-group">
                <label htmlFor="exampleInputEmail1">Tên sản phẩm:</label>
                <input
                  name="name"
                  type="text"
                  className="form-control"
                  placeholder="nhập tên sản phẩm"
                  ref={register({ required: true, maxLength: 50 })}
                />
                {errors.name?.type === "required" && "Tên không được để trống"}
                {errors.name?.type === "maxLength" && "Tên không quá 50 ký tự"}
              </div>
              <div className="form-group">
                <label htmlFor="exampleInputPassword1">Giá: </label>
                <input
                  name="price"
                  type="number"
                  className="form-control"
                  placeholder="nhập giá"
                  ref={register}
                />
              </div>
              <div className="form-group">
                <label htmlFor="exampleInputEmail1">Đường dẫn ảnh:</label>
                <input
                  name="image"
                  type="text"
                  className="form-control"
                  placeholder="nhập đường dẫn ảnh"
                  ref={register}
                />
              </div>
              {/* <div className="form-group">
                <label htmlFor="exampleInputFile">File input</label>
                <div className="input-group">
                  <div className="custom-file">
                    <input
                      type="file"
                      className="custom-file-input"
                      id="exampleInputFile"
                    />
                    <label
                      className="custom-file-label"
                      htmlFor="exampleInputFile"
                    >
                      Choose file
                    </label>
                  </div>
                  <div className="input-group-append">
                    <span className="input-group-text">Upload</span>
                  </div>
                </div>
              </div> */}
            </div>
            {/* /.card-body */}
            <div className="card-footer">
              <button type="submit" className="btn btn-primary">
                Thêm
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

ProductAdd.propTypes = {
  onAdd: PropTypes.func,
};
export default ProductAdd;
