import React, { useState } from "react";
import PropTypes from "prop-types";
import { useHistory } from "react-router-dom";
import { useForm } from 'react-hook-form';



function AddProduct({ onAdd }) {
  const {register, handleSubmit, errors} = useForm();

  let history = useHistory();
  const [valueInput, setValueInput] = useState({});
  function onHandleChange(e) {
    const { name,image,price, value } = e.target;
    setValueInput({
      ...valueInput,
      [name]: e.target.value,
      [image]: e.target.value,
      [price]: e.target.value
    });
  }
  function onSubmit(e) {
    e.preventDefault();
    onAdd(valueInput);
    history.push("/admin/products");
  }

  return (
    <div class="content-wrapper">
      <section class="content">
        <h3 className="card-title">Thêm sản phẩm</h3>
        <form>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
            <input
              type="text"
              name="name"
              className="form-control"
              id="inputProductName"
              onChange={onHandleChange}
              aria-describedby="emailHelp"
              ref={register({ required: true })}
              aria-describedby="nameHelp"
            />
            <small id="nameHelp" className="form-text text-danger">{errors.image && <span>This field is required</span>}</small>

            <label htmlFor="inputProductImage">Ảnh sản phẩm</label>
            <input
              type="text"
              name="image"
              className="form-control"
              id="inputProductImage"
              onChange={onHandleChange}
              ref={register({ required: true })}
            />
              <small id="imageHelp" className="form-text text-danger">{errors.image && <span>This field is required</span>}</small>
          </div>
          <div className="form-group">
            <label htmlFor="inputProductPrice">Giá sản phẩm</label>
            <input
              type="text"
              name="price"
              className="form-control"
              id="inputProductPrice"
              onChange={onHandleChange}
              ref={register({ required: true })}
            />

          </div>
          <button onClick={onSubmit} type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </section>
    </div>
  );
}

export default AddProduct;
